function CommentList() {
  const [comments, setComments] = useState([]);
  const [replyingTo, setReplyingTo] = useState(null);
  const [newComment, setNewComment] = useState('');
  const { username, postId } = useParams();

  async function fetchComments() {
    try {
      const response = await fetch(`http://localhost:8080/getComments?postId=${postId}`, {
        method: 'GET',
        headers: {
          'Content-type': 'application/x-www-form-urlencoded',
        },
      });
      const data = await response.json();
      setComments(data);
    } catch (error) {
      console.error('Error fetching comments:', error);
    }
  }

  async function fetchChildComments(commentId, indentLevel = 1) {
    try {
      const response = await fetch(`http://localhost:8080/getChildComments?parentId=${commentId}`, {
        method: 'GET',
        headers: {
          'Content-type': 'application/x-www-form-urlencoded',
        },
      });
      const childComments = await response.json();

      if (childComments.length > 0) {
        for (const childComment of childComments) {
          await fetchChildComments(childComment.id, indentLevel + 1);
        }

        const childCommentsWithIndent = childComments.map((childComment) => ({
          ...childComment,
          indentLevel,
        }));

        setComments((prevComments) => [...prevComments, ...childCommentsWithIndent]);
      }
    } catch (error) {
      console.error('Error fetching child comments:', error);
    }
  }

  useEffect(() => {
    fetchComments();
  }, [postId]);

  function handleReply(commentId) {
    setReplyingTo(commentId);
    setNewComment('');
  }

  function handleCancelReply() {
    setReplyingTo(null);
    setNewComment('');
  }

  async function handlePostReply(commentId) {
    try {
      console.log(`Post reply to comment ${commentId}: ${newComment}`);

      // Add the comment in the database
      await fetch('http://localhost:8080/addChildComment', {
        method: 'POST',
        body: new URLSearchParams({
          username: username,
          comment: newComment,
          parentId: commentId,
        }),
        headers: {
          'Content-type': 'application/x-www-form-urlencoded',
        },
      });

      // Fetch comments after posting a reply
      await fetchComments();

      // Fetch child comments for the comment that was just replied to
      const repliedComment = comments.find((comment) => comment.c_id === commentId);
      if (repliedComment.numbe_of_child_comments > 0) {
        await fetchChildComments(commentId, repliedComment.indentLevel);
      }

      // Close reply prompt
      handleCancelReply();
    } catch (error) {
      console.error('Error posting reply:', error);
    }
  }

  return (
    <div className="commentsListClass">
      <p>Posted comments:</p>
      {comments.map((comment) => (
        <div key={comment.id} className={`comment-item indent-${comment.indentLevel}`}>
          <div className="comment-content">
            <p>
              <span className="comment-username">{comment.createdBy}:</span> {comment.comment}
            </p>
          </div>
          <div className="reply-button-container">
            <button className="reply-button" onClick={() => handleReply(comment.c_id)}>
              Reply <i className="fa fa-reply"></i>
            </button>
          </div>
          {comment.numbe_of_child_comments > 0 && (
            <div className={`child-comments-container indent-${comment.indentLevel + 1}`}>
              {comments
                .filter((childComment) => childComment.parentId === comment.c_id)
                .map((childComment) => (
                  <div key={childComment.id} className={`comment-item indent-${childComment.indentLevel}`}>
                    <div className="comment-content">
                      <p>
                        <span className="comment-username">{childComment.createdBy}:</span> {childComment.comment}
                      </p>
                    </div>
                  </div>
                ))}
            </div>
          )}
          {replyingTo === comment.c_id && (
            <div className="reply-input-container">
              <input
                type="text"
                placeholder="Type your reply..."
                value={newComment}
                onChange={(e) => setNewComment(e.target.value)}
              />
              <button className="post-reply-button" onClick={() => handlePostReply(comment.c_id)}>
                Post Reply
              </button>
              <button className="cancel-reply-button" onClick={handleCancelReply}>
                Cancel
              </button>
            </div>
          )}
        </div>
      ))}
    </div>
  );
}

export default CommentList;
