import React, { useEffect, useState } from "react";
import { useParams, Link } from "react-router-dom";

function ShowProfile() {
  const { username } = useParams();
  const [profile, setProfile] = useState([]);
  const [isChangingPassword, setChangingPassword] = useState(false);
  const [currentPassword, setCurrentPassword] = useState("");
  const [newPassword, setNewPassword] = useState("");

  useEffect(() => {
    const getProfile = async () => {
      try {
        const response = await fetch(
          `http://localhost:8080/getProfile?username=${username}`
        );
        const data = await response.json();
        setProfile(data);
      } catch (error) {
        console.error("Error fetching data:", error);
      }
    };

    getProfile();
  }, [username]);

  const handlePasswordChange = async () => {
    // Check if the current password matches (you may need to implement this on the server side)
    const isCurrentPasswordCorrect = await checkCurrentPassword(currentPassword);

    if (isCurrentPasswordCorrect) {
      // Allow the user to enter a new password and change it (you may need to implement this on the server side)
      await changePassword(newPassword);
      // Optionally reset the password input fields
      setCurrentPassword("");
      setNewPassword("");
      // Toggle the password change state
      setChangingPassword(false);
    } else {
      // Handle incorrect current password
      console.error("Incorrect current password");
    }
  };

  const checkCurrentPassword = async (password) => {
    // Implement logic to check if the provided current password is correct
    // You may need to make a request to the server to verify the password
    // Return true if the password is correct, otherwise return false
    // This is a placeholder function, replace it with your actual logic
    return true;
  };

  const changePassword = async (newPassword) => {
    // Implement logic to change the user's password
    // You may need to make a request to the server to update the password
    // This is a placeholder function, replace it with your actual logic
    console.log("Changing password to:", newPassword);
  };

  return (
    <>
      <h2>User Profile: {username}: </h2>
      <div>
        <h7>username: {profile.username}</h7>
        <br />
        <h7>name: {profile.firstName + profile.lastName}</h7>
        <br />
        <h7>badge: {profile.badge}</h7>
      </div>
      <br />
      {isChangingPassword ? (
        <>
          <div>
            <label>
              Current Password:
              <input
                type="password"
                value={currentPassword}
                onChange={(e) => setCurrentPassword(e.target.value)}
              />
            </label>
          </div>
          <div>
            <label>
              New Password:
              <input
                type="password"
                value={newPassword}
                onChange={(e) => setNewPassword(e.target.value)}
              />
            </label>
          </div>
          <button onClick={handlePasswordChange}>Change Password</button>
        </>
      ) : (
        <button onClick={() => setChangingPassword(true)}>Change Password</button>
      )}
      <br />
      <Link to={`/getChannels/${username}`}>Channels</Link>
      <br />
    </>
  );
}

export default ShowProfile;
