import React, { useState } from 'react';
import { Link, useNavigate } from 'react-router-dom';
import './Login.css';

const Login = () => {
  const [username, setUsername] = useState('');
  const [password, setPassword] = useState('');

  const handleLogin = async () => {
    // Make a POST request to your server endpoint (/getUser)
    const response = await fetch('http://localhost:8080/getUser', {
      method: 'POST',
      headers: {
        'Content-Type': 'application/x-www-form-urlencoded',
      },
      body: new URLSearchParams({
        username: username,
        password: password,
      }),
    });

    const data = await response.json();
    if (response.ok) {
      // Successful login
      // Redirect to the Channels component or perform any other actions
      alert('Login successful');
    } else {
      // Failed login
      alert(data.message);
    }

    // console.log(result); // Assuming your server sends a user ID or error message

    // Handle the response accordingly, e.g., redirect to a dashboard or show an error.
  };

  return (
    <div>
      <h2>Login</h2>
      <form>
        <label>
          Username:
          <input type="text" value={username} onChange={(e) => setUsername(e.target.value)} />
        </label>
        <br />
        <label>
          Password:
          <input type="password" value={password} onChange={(e) => setPassword(e.target.value)} />
        </label>
        <br />
        <button type="button" onClick={handleLogin}>
          Login
        </button>
        <div className="link">
          New to somename? <Link to="/signup">Create an account</Link>
        </div>
      </form>
    </div>
  );
};

export default Login;
