import { useState, useEffect } from "react";



function Channels({ postId })
{
    const [getList, setList] = useState([]);
    // To call geCommentst() everytime on load
    useEffect(() => {
        getChannels();
    });
    function addChannel()
    {
        fetch('http://localhost:8080/getChannels',
        {
            method: 'POST',
            body: new URLSearchParams({
                name: "CMPT353",
                description: "CMPT 353 related information."
            }),
        });
    }
    function getChannels()
    {
        fetch('http://localhost:8080/getChannels',
        {
            method: 'GET',
            // body: new URLSearchParams({
            //     postId: postId
            //     }),
            headers: {
                "Content-type": "application/x-www-form-urlencoded"
                }
        }
        ).then(response => response.json()).then(response => setList(response)).catch(error => console.error(error))
        // console.log(getList);
    }
    return(
        <div className="channelsListClass" >
            {getList.map(channel => (
            <div>
                <p> {channel.Name}</p>
            </div>
            ))}
            <button onClick={addChannel}>add</button>
        </div>
    )
};

export default Channels;
