import React, { useState } from 'react';
import './Search.css';

const Search = () => {
  const [searchType, setSearchType] = useState('content');
  const [searchTerm, setSearchTerm] = useState('');

  const handleSearchTypeChange = (type) => {
    setSearchType(type);
  };

  const handleSearch = () => {
    // Perform the search based on searchType and searchTerm
    console.log(`Searching for ${searchType}: ${searchTerm}`);
    // Add your logic here to handle the search
  };

  return (
    <div className="search-container">
      <input
        type="text"
        value={searchTerm}
        onChange={(e) => setSearchTerm(e.target.value)}
        placeholder={`Search ${searchType === 'content' ? 'Content' : 'User'}`}
      />
      <div className="search-options">
        <label>
          <input
            type="radio"
            value="content"
            checked={searchType === 'content'}
            onChange={() => handleSearchTypeChange('content')}
          />
          Content
        </label>
        <label>
          <input
            type="radio"
            value="user"
            checked={searchType === 'user'}
            onChange={() => handleSearchTypeChange('user')}
          />
          User
        </label>
      </div>
      <button onClick={handleSearch}>Search</button>
    </div>
  );
};

export default Search;
