import { useState, useEffect } from "react";
import { Link, useParams } from "react-router-dom";


function CommentLists({postId})
{
    const [getList, setList] = useState([]);
    // To call geCommentst() everytime on load
    useEffect(() => {
        getComments();
    });
    function getComments()
    {
        fetch('http://localhost:8080/getComments?postId='+postId,
        {
            method: 'GET',
            // body: new URLSearchParams({
            //     postId: postId
            //     }),
            headers: {
                "Content-type": "application/x-www-form-urlencoded"
                }
        }
        ).then(response => response.json()).then(response => setList(response)).catch(error => console.error(error))
        // console.log(getList);
    }
    return(
        <div className="commentsListClass" >
            get posted comments here
            {getList.map(comment => (
            <div>                
                <p> {comment.comment}</p>
            </div>
            ))}
        </div>
    )
};

export default CommentLists;
