import React, { useState, useEffect } from 'react';
import { useParams } from 'react-router-dom';
import './CommentList.css'; // Import your CSS file

function CommentList() {
  const [comments, setComments] = useState([]);
  const { postId } = useParams();

  useEffect(() => {
    getComments();
  }); // Include postId in the dependency array

  function getComments() {
    fetch(`http://localhost:8080/getComments?postId=${postId}`, {
      method: 'GET',
      headers: {
        'Content-type': 'application/x-www-form-urlencoded',
      },
    })
      .then((response) => response.json())
      .then((response) => setComments(response))
      .catch((error) => console.error(error));
  }

  // To fetch the child comments if there are any
  function fetchChildComments(commentId, indentLevel = 1) {
    fetch(`http://localhost:8080/getChildComments?commentId=${commentId}`, {
      method: 'GET',
      headers: {
        'Content-type': 'application/x-www-form-urlencoded',
      },
    })
      .then((response) => response.json())
      .then((childComments) => {
        // Recursively fetch and display child comments
        childComments.forEach((childComment) => {
          fetchChildComments(childComment.id, indentLevel + 1); // Increase the indent level for each level of nesting
        });
        setComments([...comments, { ...childComments[0], indentLevel }]); // Update state with both parent and child comments
      })
      .catch((error) => console.error(error));
  }

  return (
    <div className="commentsListClass">
      <p>Posted comments:</p>
      {comments.map((comment) => (
        <div key={comment.id} className="comment-item">
          <p>
            <span className="comment-username">{comment.createdBy}:</span> {comment.comment}
          </p>
          {comment.numbe_of_child_comments > 0 && fetchChildComments(comment.id, comment.indentLevel)}
        </div>
      ))}
    </div>
  );
}

export default CommentList;
