import { useState } from "react";
import { Route, Routes } from "react-router-dom";
import Landing from "./Landing";
import AddPosts from './AddPosts.js';
import PostList from './PostList.js'
import Channels from "./Channels.js";
import AddChannel from "./AddChannel.js";
import Comment from "./Comment.js";
import Signup from "./Signup.js";
import Login from "./Login.js";

function App() {
  const [isAuthenticated, setIsAuthenticated] = useState(false);

  function setAuthenticated()
  {
    setIsAuthenticated(true);
    // alert("reached here");
    // console.log("reached");
  }
  // const [getList, setList] = useState([]);
  // // if(getList.length <1) 
  // function get() 
  // {
  //   fetch('http://localhost:8080/getPosts').then(response => response.json()).then(response => setList(response)).catch(error => console.error(error))
  // };
  return (
    <Routes>
      <Route path="/" element={<Landing setAuthenticated = {setAuthenticated}/>}/>
      <Route path="/login" element = {<Login  setAuthenticated = {setAuthenticated} />} />
      <Route path='/addPost/:channelId' element = {<AddPosts />} />
      <Route path='/getPosts/:channelId' element = {<PostList />} />
      <Route path="/getChannels" element = {isAuthenticated ? <Channels /> : <Login />} />
      <Route path="/addChannel" element = {<AddChannel />} />
      <Route path="/addComment" element = {<Comment />} />
      <Route path="/signup" element = {<Signup />} />

    </Routes>
  );
}

export default App;
