import React, { useState, useEffect } from 'react';
import { Link, useParams } from 'react-router-dom';
import './CommentLists.css'; // Import your CSS file

function CommentLists({ postId }) {
  const [getList, setList] = useState([]);

  useEffect(() => {
    getComments();
  }, []);

  function getComments() {
    fetch('http://localhost:8080/getComments?postId=' + postId, {
      method: 'GET',
      headers: {
        'Content-type': 'application/x-www-form-urlencoded',
      },
    })
      .then((response) => response.json())
      .then((response) => setList(response))
      .catch((error) => console.error(error));
  }

  return (
    <div className="commentsListClass">
      <p>Posted comments:</p>
      {getList.map((comment) => (
        <div key={comment.id} className="comment-item">
          <p>
            <span className="comment-username">{comment.createdBy}:</span> {comment.comment}
          </p>
        </div>
      ))}
    </div>
  );
}

export default CommentLists;
