import { useState, useEffect } from "react";
import { Link } from "react-router-dom";


function Channels()
{
    const [getList, setList] = useState([]);
    // To call geCommentst() everytime on load
    useEffect(() => {
        getChannels();
    });
    function getChannels()
    {
        fetch('http://localhost:8080/getChannels',
        {
            method: 'GET',
            // body: new URLSearchParams({
            //     postId: postId
            //     }),
            headers: {
                "Content-type": "application/x-www-form-urlencoded"
                }
        }
        ).then(response => response.json()).then(response => setList(response)).catch(error => console.error(error))
        // console.log(getList);
    }
    return(
        <>
        <div className="channelsListClass">
            {getList.map(channel => (
                <Link to='/getPosts'> channelId = channel.id
                    <p>{channel.name}</p>
                    <p> {channel.description} </p>
                </Link>
            ))}
        </div>
        <Link to='/addChannel'>Add</Link>
        <br></br>
        <Link to='/'>Landings</Link>
        </>
    )
};

export default Channels;
