import { Link, useParams } from "react-router-dom";
import { useState, useEffect } from "react";
import Post from "./Post";
import "./PostList.css";

function PostList() {
  const [getList, setList] = useState([]);
  
  const {username, channelId} = useParams();
  // To call get() everytime on load
  useEffect(() => {
    get();
  });
  function get()
  {
    fetch(
      'http://localhost:8080/getPosts?channelId='+channelId+'&username='+username
    ).then(response => response.json()).then(response => setList(response)).catch(error => console.error(error))
  }
    return (
      <>
        Get all the Posts here
        {getList.map(post => (
          <div className="container">
            <Post post={post} />
          </div>
        ))}
        <br></br>
        <Link to={`/addPost/${channelId}`}>Add Post</Link>
        <br></br>
        <Link to={`/getChannels/${username}`}>channels</Link>
        <br></br>
        <Link to='/'>Landings</Link>
      </>
    );
  }
  
  export default PostList;
