import React, { useState, useEffect } from "react";
import { useParams } from "react-router-dom";
import PostData from "./PostData";
import PostID from "./PostID";
import PostTopic from "./PostTopic";
import CommentSection from "./CommentSection";
import "./Post.css"

function Post() {
  const { username, channelId, postId } = useParams();
  const [post, setPost] = useState({});
  const [likesCount, setLikesCount] = useState(0);

  useEffect(() => {
    fetchPostDetails();
    fetchLikesCount();
  }, []);

  const fetchPostDetails = async () => {
    console.log("fetching posts");
    try {
      const response = await fetch(`http://localhost:8080/getPost?postId=${postId}`);
      const data = await response.json();
      alert(data);
      setPost(data[0]); // Assuming the response is an array with one post
    } catch (error) {
      console.error("Error fetching post details:", error);
    }
  };

  const fetchLikesCount = async () => {
    try {
      const response = await fetch(`http://localhost:8080/getLikesCount/${postId}`);
      const data = await response.json();
      setLikesCount(data.likesCount);
    } catch (error) {
      console.error("Error fetching likes count:", error);
    }
  };

//   return (
//     <div className="post-container">
//       <PostID id={post.id} />
//       <PostTopic topic={post.topic} />
//       <PostData data={post.data} />

//       {/* Display Likes Count */}
//       <p>Likes: {likesCount}</p>

//       {/* Display Comments Section */}
//       <CommentSection postId={postId} username={username} />
//     </div>
//   );


return (
    <div className="post-container">
      <div className="post-header">
        <PostTopic topic={post.topic} />
      </div>
      <div className="post-content">
        <PostData data={post.data} />
      </div>
      <div className="comment-section">
        <CommentSection postId={post.id} username={username} />
      </div>
    </div>
  );
}

export default Post;
