import React, { useState, useEffect } from 'react';
import { useParams } from 'react-router-dom';
import './CommentList.css';

function CommentList() {
  const [comments, setComments] = useState([]);
  const [childComments, setChildComments] = useState({});
  const [replyingTo, setReplyingTo] = useState(null);
  const [newComment, setNewComment] = useState('');
  const { username, postId } = useParams();

  async function getComments() {
    const response = await fetch(`http://localhost:8080/getComments?postId=${postId}`, {
      method: 'GET',
      headers: {
        'Content-type': 'application/x-www-form-urlencoded',
      },
    });

    const data = await response.json();
    setComments(data);

    // Fetch child comments for each parent comment
    const childCommentsMap = {};
    await Promise.all(
      data.map(async (comment) => {
        if (comment.numbe_of_child_comments > 0) {
          const childResponse = await fetch(`http://localhost:8080/getChildComments?parentId=${comment.c_id}`, {
            method: 'GET',
            headers: {
              'Content-type': 'application/x-www-form-urlencoded',
            },
          });

          const childData = await childResponse.json();
          childCommentsMap[comment.c_id] = childData;
        }
      })
    );

    setChildComments(childCommentsMap);
  }

  useEffect(() => {
    getComments();
  }, [postId]);

  function handleReply(commentId) {
    setReplyingTo(commentId);
    setNewComment('');
  }

  function handleCancelReply() {
    setReplyingTo(null);
  }

  async function handlePostReply(commentId) {
    console.log(`Post reply to comment ${commentId}: ${newComment}`);

    await fetch('http://localhost:8080/addChildComment', {
      method: 'POST',
      body: new URLSearchParams({
        username: username,
        comment: newComment,
        parentId: commentId,
      }),
      headers: {
        'Content-type': 'application/x-www-form-urlencoded',
      },
    });

    // After posting, fetch the updated comments
    await getComments();

    setReplyingTo(null);
    setNewComment('');
  }

  return (
    <div className="commentsListClass">
      <p>Posted comments:</p>
      {comments.map((comment) => (
        <div key={comment.id} className={`comment-item indent-${comment.indentLevel}`}>
          <div className="comment-content">
            <p>
              <span className="comment-username">{comment.createdBy}:</span> {comment.comment}
            </p>
          </div>
          <div className="reply-button-container">
            <button className="reply-button" onClick={() => handleReply(comment.c_id)}>
              Reply <i className="fa fa-reply"></i>
            </button>
          </div>
          {replyingTo === comment.c_id && (
            <div className="reply-input-container">
              <input
                type="text"
                placeholder="Type your reply..."
                value={newComment}
                onChange={(e) => setNewComment(e.target.value)}
              />
              <button className="post-reply-button" onClick={() => handlePostReply(comment.c_id)}>
                Post Reply
              </button>
              <button className="cancel-reply-button" onClick={handleCancelReply}>
                Cancel
              </button>
            </div>
          )}
          {childComments[comment.c_id] && childComments[comment.c_id].length > 0 && (
            <div className={`child-comments indent-${comment.indentLevel + 1}`}>
              {childComments[comment.c_id].map((childComment) => (
                <div key={childComment.id} className={`comment-item indent-${childComment.indentLevel}`}>
                  <div className="comment-content">
                    <p>
                      <span className="comment-username">{childComment.createdBy}:</span> {childComment.comment}
                    </p>
                  </div>
                </div>
              ))}
            </div>
          )}
        </div>
      ))}
    </div>
  );
}

export default CommentList;
