import { useParams, Link } from "react-router-dom";
import "./SearchResultPostList"
import { useEffect } from "react";

function SearchResultPostList()
{
    var { searchTerm, username } = useParams();

    var postList = [];

    async function getPosts()
    {
        const response = await fetch(`http://localhost:8080/searchContent?searchTerm=${searchTerm}`);
        
        postList = await response.json();
    }

    useEffect(()=>
    getPosts(),[postList])

    return(
        <>
            <h2>Search result posts: </h2>
            <div className="post-list">
                {postList.map(post => (
                <div key={post.id} className="post-card">
                    <h3>{post.topic}</h3>
                    <Link to={`/gotoPost/${username}/${post.channel_id}/${post.id}`}>Read More</Link>
                </div>
                ))}
            </div>
            <br />
            <Link to={`/getChannels/${username}`}>Channels</Link>
            <br />
        </>
    );
}

export default SearchResultPostList;
